import React, { useState } from 'react';

const Gallery = () => {
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [lightboxImageIndex, setLightboxImageIndex] = useState(0);
  const images = [
    './img/image1.png',
    'img/image2.png',
    'img/image3.png',
    'img/image4.png',
    'img/image5.png',
  ];

  const handleImageClick = (index) => {
    setLightboxOpen(true);
    setLightboxImageIndex(index);
  };

  const handleLightboxClose = () => {
    setLightboxOpen(false);
  };

  const handleLightboxPrev = () => {
    setLightboxImageIndex((prevIndex) => (prevIndex + images.length - 1) % images.length);
  };

  const handleLightboxNext = () => {
    setLightboxImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  return (
    <div className="gallery">
      {images.map((image, index) => (
        <img
          key={index}
          src={images}
          alt={`Image ${index + 1}`}
          onClick={() => handleImageClick(index)}
        />
      ))}
      {lightboxOpen && (
        <div className="lightbox">
          <div className="lightbox-overlay" onClick={handleLightboxClose}></div>
          <div className="lightbox-content">
            <img src={images[lightboxImageIndex]} alt={`Image ${lightboxImageIndex + 1}`} />
            <button className="lightbox-prev" onClick={handleLightboxPrev}>
              Prev
            </button>
            <button className="lightbox-next" onClick={handleLightboxNext}>
              Next
            </button>
            <button className="lightbox-close" onClick={handleLightboxClose}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Gallery;
