import React, { useState } from 'react';
// import './Gallery.css';

const Gallery = ({ images }) => {
  const [photoIndex, setPhotoIndex] = useState(0);
  const [lightboxIsOpen, setLightboxIsOpen] = useState(false);

  const handleImageClick = (index) => {
    setPhotoIndex(index);
    setLightboxIsOpen(true);
  };

  return (
    <div className="gallery">
      <div className="gallery-grid">
        {images.map((image, index) => (
          <div key={index} className="gallery-item" onClick={() => handleImageClick(index)}>
            <img src={image.thumbnailUrl} alt={image.title} />
          </div>
        ))}
      </div>
      {lightboxIsOpen && (
        <div className="lightbox-overlay" onClick={() => setLightboxIsOpen(false)}>
          <div className="lightbox-content">
            <img src={images[photoIndex].url} alt={images[photoIndex].title} />
            <div className="lightbox-nav">
              <button onClick={() => setPhotoIndex((photoIndex + images.length - 1) % images.length)}>
                {'<'}
              </button>
              <button onClick={() => setLightboxIsOpen(false)}>x</button>
              <button onClick={() => setPhotoIndex((photoIndex + 1) % images.length)}>
                {'>'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Gallery;
